import React from "react";
import { useState } from 'react';
import Footer from "../Components/footer";
import NavBarCompany from "../Components/navbarcompany";
import Popup from "../Components/popup"
import "../css/companyPage.css"


function getUnique(arr, index) {
  const unique = arr
       .map(e => e[index])
       // store the keys of the unique objects
       .map((e, i, final) => final.indexOf(e) === i && i)
       // eliminate the dead keys & store unique objects
      .filter(e => arr[e]).map(e => arr[e]);
   return unique;
}

export default function CompanyPage(){

    //popup button
    const [buttonPopup, setButtonPopup] = useState(false);

    //popup info
    const [popupUserName, setPopupUserName] = useState('');
    const [popupUserEmail, setPopupUserEmail] = useState('');
    const [popupUserContracts, setPopupUserContracts] = useState([]);

    const companyID = localStorage.getItem('userID');
    const companyName = localStorage.getItem('companyName');

    // the value of the search field
    const [name, setName] = useState('');

    // the search result
    const [foundUsers, setFoundUsers] = useState('');

    // loaded users
    const [loadedUsers, setLoadedUsers] = useState('');

    // loaded contracts
    const [loadedContracts, setLoadedContracts] = useState('');

    function handlePopup(event, username, useremail, contracts) {
      event.preventDefault();
      setPopupUserName(username);
      setPopupUserEmail(useremail);

      let user_contracts = [];

      for (let i = 0; i < contracts.length; ++i) {
        for(let j = 0; j < loadedContracts.length; ++j) {
          if(contracts[i] == loadedContracts[j]._id) user_contracts.push(loadedContracts[j]);
        }
      }
      console.log(loadedContracts);
      setPopupUserContracts(user_contracts);


      setButtonPopup(true);

    }

    // load users and contracts
     React.useEffect(() => {
        fetch("https://vast-peak-05541.herokuapp.com/api/users", {
          method:'GET',
          headers:{
              "Content-Type":'application/json',
          }
        }).then(response => response.json())
        .then(data => {
          let users = getUnique(data, 'name');
          setFoundUsers(users);
          setLoadedUsers(users);
        });
        fetch("https://vast-peak-05541.herokuapp.com/api/contracts", {
          method:'GET',
          headers:{
              "Content-Type":'application/json',
          }
        }).then(response => response.json())
          .then(data => {
            setLoadedContracts(data);
            console.log(data);
          });

      //   let new_user = {
      //       userassignedContracts : assignedContract
      //   };
      //   fetch("https://vast-peak-05541.herokuapp.com/api/users/6272820ff942f9bb2b0bb928/deletecontract", {
      //     body: JSON.stringify(new_user),
      //     method:'PUT',
      //     headers:{
      //         "Content-Type":'application/json',
      //     }
      // }).then(response => response.json())
      //   .then(data => {
      //     console.log(data);
      //   });
       }, []);

    const filter = (e) => {
      const keyword = e.target.value;

      if (keyword !== '') {
        const results = foundUsers.filter((user) => {
          return user.name.toLowerCase().startsWith(keyword.toLowerCase());
          // Use the toLowerCase() method to make it case-insensitive
        });
        setFoundUsers(results);
      } else {
        setFoundUsers(loadedUsers);
        // If the text field is empty, show all users
      }
      setName(keyword);
    };
    return (
        <>
        <NavBarCompany></NavBarCompany>
        <div className="about-section" width="100%">
            <h1>{companyName}</h1>
            <p>{companyID}</p>
        </div>
        <div className="container search-wrapper">
        <h2>Users</h2>
        <div className="rounded">
          <input type="search" value={name} onChange={filter} className="input form-control rounded" placeholder="Search" aria-label="Search" aria-describedby="search-addon" />
        </div>
        <div className="row">
        	<div className="col-lg-12">
        		<div className="main-box clearfix">
        			<div className="table-responsive">
                <table className="table user-list">
                  <thead>
                    <tr>
                      <th><span>User</span></th>
                      <th><span>Email</span></th>
                      <th>&nbsp;</th>
                    </tr>
                  </thead>
                  <tbody>
                    {foundUsers && foundUsers.length > 0 ? (
                      foundUsers.map((user) => (
                        <tr key={user.id} className="user">
                          <td className="user-id">{user.name}</td>
                          <td className="user-name">{user.email}</td>
                          <td>
                            <a onClick={(e) => handlePopup(e, user.name, user.email, user.assignedContracts)} className="table-link">
            									<span className="fa-stack">
            										<i className="fa fa-square fa-stack-2x"></i>
            										<i className="fa fa-search-plus fa-stack-1x fa-inverse"></i>
            									</span>
            								</a>
                          </td>
                        </tr>
                      ))
                    ) : (
                      <tr>
                        <td></td>
                        <td></td>
                        <td></td>
                      </tr>
                    )}
                  </tbody>
                  </table>
          			</div>
          		</div>
          	</div>
          </div>
        </div>
        <Footer></Footer>
        <Popup trigger={buttonPopup} setTrigger={setButtonPopup}>
          <div className="popup-user-info">
            <div className="row name">
              {popupUserName}
            </div>
            <div className="row">
              {popupUserEmail}
            </div>
          </div>
          <div className="row">
            <div className="col-lg-12">
              <div className="main-box clearfix">
                <div className="table-responsive">
                  <table className="table user-list">
                    <thead>
                      <tr>
                        <th><span>Contract</span></th>
                        <th><span>Status</span></th>
                      </tr>
                    </thead>
                    <tbody>
                      {popupUserContracts && popupUserContracts.length > 0 ? (
                        popupUserContracts.map((contract) => (
                          <tr key={contract._id} className="user">
                            <td className="user-id">{contract.name}</td>
                            <td className="user-name"></td>
                          </tr>
                        ))
                      ) : (
                        <tr>
                          <td></td>
                          <td></td>
                          <td></td>
                        </tr>
                      )}
                    </tbody>
                    </table>
                  </div>
                </div>
              </div>
            </div>
        </Popup>
        </>
    );
}
