import React from "react";
import { useState } from 'react';
import Footer from "../Components/footer";
import NavBarCompany from "../Components/navbarcompany";
import "../css/companyPage.css"

var users = null;

export default function CompanyPage(){

    const companyID = localStorage.getItem('userID');

    // the value of the search field
    const [name, setName] = useState('');

    async function getUsersApi() {
        let result = await fetch("https://vast-peak-05541.herokuapp.com/api/companies", {
            method:'GET',
            headers:{
                "Content-Type":'application/json',
            }
        }).then(response => response.json());
        //let json = await result.json(result);
        console.log(result);
        users = result
    };

    getUsersApi();
    console.log(users)

    // the search result
    const [foundUsers, setFoundUsers] = useState('');

    const filter = (e) => {
      const keyword = e.target.value;

      if (keyword !== '') {
        const results = this.state.users.filter((user) => {
          return user.name.toLowerCase().startsWith(keyword.toLowerCase());
          // Use the toLowerCase() method to make it case-insensitive
        });
        setFoundUsers(results);
      } else {
        setFoundUsers(this.state.users);
        // If the text field is empty, show all users
      }

      setName(keyword);
    };
    return (
        <>
        <NavBarCompany></NavBarCompany>
        <div id="homeCompany" style={{'marginLeft': '20px'}}>
            <h4>{companyID}</h4>
            <ul>
                <li>Contacts tracking</li>
                <li>Pending contracts</li>
                <li>General information</li>
                <li>Overview of the companys's status</li>
            </ul>
        </div>
        <div className="container">
        <div className="input-group rounded">
          <input type="search" value={name} onChange={filter}className="input form-control rounded" placeholder="Search" aria-label="Search" aria-describedby="search-addon" />
        </div>
        <div className="row">
        	<div className="col-lg-12">
        		<div className="main-box clearfix">
        			<div className="table-responsive">

                    {foundUsers && foundUsers.length > 0 ? (
                      foundUsers.map((user) => (
                      <table className="table user-list">
                        <thead>
              						<tr>
              							<th><span>User</span></th>
              							<th><span>Email</span></th>
                            <th><span>Age</span></th>
                            <th>&nbsp;</th>
              						</tr>
              					</thead>
                        <tbody>
                        <tr key={user.id} className="user">
                          <td className="user-id">{user.id}</td>
                          <td className="user-name">{user.name}</td>
                          <td className="user-age">{user.age} year old</td>
                          <td>
                            <a href="#" className="table-link">
            									<span className="fa-stack">
            										<i className="fa fa-square fa-stack-2x"></i>
            										<i className="fa fa-search-plus fa-stack-1x fa-inverse"></i>
            									</span>
            								</a>
                          </td>
                        </tr>
                      </tbody>
                    </table>
                      ))
                    ) : (
                      <p>No users found!</p>

                    )}
          			</div>
          		</div>
          	</div>
          </div>
        </div>
        <Footer></Footer>
        </>
    );
}
